import React, { useEffect, useState } from 'react'

export const PruebasComponent = () => {

const [usuario, setUsuario] = useState("inavarrete");
const [fecha, setFecha] = useState("01-01-1998");
const [contador, setContador] = useState(0);

const modUsuario = e => {
    setUsuario(e.target.value);
};

const cambiarFecha = e => {

    setFecha(Date.now())
}

useEffect(() => {
    console.log("Has cargado el componente")
}, []);

useEffect(() => {
    setContador(contador+1);
    console.log("Has modificado el usuario: " + contador);
}, [usuario]);

  return (
    <div>
        <h1>El efecto - Hook useEffect</h1>
        <strong className='label'>{usuario}</strong>
        <strong>{fecha}</strong>

        <p>
            <input type='text' onChange={ modUsuario} placeholder='Cambiar el nombre'/>
            <button onClick={ cambiarFecha }>Cambiar fecha</button>
        </p>
        
    </div>
  )
}
