    <div>
        <h1>El efecto - Hook useEffect</h1>
        <strong className='label'>{usuario}</strong>
        <input type='text' onChange={ modUsuario} placeholder='Cambiar el nombre'/>

        <button onClick={ cambiarFecha }>Cambiar fecha</button>
    </div>
  )
}
