import React, { useState } from 'react'

export const Crear = () => {

    const titulo = "Añadir película";
    const [peliState, setPeliState]= useState({
        titulo:'', 
        descripcion:''
    });

    const conseguirDatosForm = e => {
        e.preventDefault();

        let target = e.target;
        let titulo = target.titulo.value;
        let descripcion = target.descripcion.value;

        let peli = {
            id: new Date().getTime(),
            titulo, 
            descripcion
        }

        setPeliState(peli);

    }

    return (
        <div className="add">
            <h3 className="title">{titulo}</h3>

            {peliState.titulo}

            <form onSubmit={conseguirDatosForm}>
                <input type="text"
                    id="titulo"
                    name='titulo'
                    placeholder="Titulo"
                />

                <textarea
                    id="descripcion"
                    name='descripcion'
                    placeholder="Descripcion" >
                </textarea>

                <input
                    type="submit"
                    id="save"
                    value="Guardar" />
            </form>
        </div>
    )
}
