import React, { useEffect, useReducer } from 'react'
import { JuegoReducer } from '../reducer/JuegoReducer';

const init = () => {
    return JSON.parse(localStorage.getItem("juegos")) || [];
}

export const MisJuegos = () => {

    const [juegos, dispatch] = useReducer(JuegoReducer, [], init)

    useEffect(() => {
        localStorage.setItem("juegos", JSON.stringify(juegos));
    }, [juegos])

    const conseguirDatosForm = (e) => {
        e.preventDefault();

        let juego = {
            id: new Date().getTime(),
            titulo: e.target.titulo.value,
            descripcion: e.target.descripcion.value
        }

        const accion = {
            type: "crear",
            payload: juego
        }

        dispatch(accion);

    }

    const borramelo = (id) => {

        const action = {
            type: "borrar",
            payload: id
        }

        console.log(action);

        dispatch(action);
    }

    const editar = (e, id) => {

        let juego = {
            id,
            titulo: e.target.value,
            descripcion: e.target.value
        };

        const action = {
            type: "editar",
            payload: juego
        }

        console.log(action);

        dispatch(action);

    }

    return (
        <div>
            <h1>Estos son mis videojuegos</h1>

            <p>Numero de videojuegos: {juegos.length}</p>
            <ul>
                {
                    juegos.map(juego => (
                        <li key={juego.id}>
                            {juego.titulo}<button onClick={e => borramelo(juego.id)}>X</button>
                            <input type='text' onBlur={e => editar(e, juego.id)}
                                onKeyPress={e => {
                                    if (e.key == "Enter") {
                                        editar(e, juego.id)
                                    }
                                }} 
                            />

                        </li>
                    ))
                }
            </ul>

            <h3>Agregar juego</h3>

            <form onSubmit={conseguirDatosForm}>
                <input type='text' name='titulo' placeholder='Titulo' />
                <textarea name='descripcion' placeholder='Descripcion' />
                <input type='submit' value="Guardar" />
            </form>

        </div>
    )
}
