import React, { useState } from 'react'

export const useMayus = (texto) => {

    const [miTexto, setMiTexto] = useState("Isaias Navarrete");

    const mayusculas = () => {
        return texto.toUpperCase();
    }

    const minusculas = () => {
        return texto.toLowerCase();
    }

    const concatenar = (added) => {
        return texto + added;

    }


    return {
        mayusculas,
        minusculas,
        concatenar
    };

}
