import React, { useState } from 'react'
import { useMayus } from '../hooks/useMayus'

export const PruebasCustom = () => {

    const [miTexto, setMiTexto] = useState("Isaias Navarrete");

    const {mayusculas, minusculas, concatenar} = useMayus(miTexto);

    mayusculas();


  return (
    <div>
        <h1>Probando componentes personalizados</h1>
        {miTexto}

        <button onClick={ (e) => {
            setMiTexto(mayusculas())
        }}>Poner en mayusculas</button>


    </div>
  )
}
