import React, { useEffect, useState } from 'react'

export const MiUsuario = () => {

    const [usuario, setUsuario] = useState({
        datos: null
    });

    const getUsuario = async(url) => {

        const peticion = await fetch(url, {
                method: "GET",
                headers: {
                    "Content-Type": "application/json",
                    "x-api-key": "reqres-free-v1"
                }
            });

        const datos = await peticion.json();

        setUsuario({datos: datos.data})
    }


    const getId = e => {

        let id = parseInt(e.target.value);

        let url = "https://reqres.in/api/users/" + id;

        getUsuario(url);

    }

    useEffect(() => {
        let url = "https://reqres.in/api/users/1";

        getUsuario(url);

    },[]);


    return (
        <div>
            <h1>Mi usuario:</h1>
            <p>Datos del usuario: </p>
            <p>{usuario?.datos?.first_name} {usuario?.datos?.last_name}</p>

            <input type='number' name='id' onChange={getId} />
        </div>
    )
}
